import React, { useState } from "react";
import Link from "next/link";
import { Menu, Input, Row, Col } from "antd";
import PropTypes from "prop-types";

import userProfile from "../components/userProfile";
import LoginForm from "../components/LoginForm";

// 모든 페이지에서 공통인 것은 _app.js에 넣고, 특정 컴포넌트끼리 공통인 것은 AppLayout.js에 넣는다.
const AppLayout = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <div>
      <Menu mode="horizontal">
        <Menu.Item>
          <Link href="/">
            <a>노드버드</a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Link href="/profile">
            <a>프로필</a>
          </Link>
        </Menu.Item>
        <Menu.Item>
          <Input.Search enterButton style={{ verticalAlign: "middle" }} />
        </Menu.Item>
        <Menu.Item>
          <Link href="/signup">
            <a>회원가입</a>
          </Link>
        </Menu.Item>
      </Menu>
      {/*컬럼 사이에 간격을 주는 것이 gutter*/}
      <Row gutter={8}>
        {/*xs: 모바일, sm: 테블릿, md: 작은 데스크탑*/}
        {/*xs인 24가 전체이다. 따라서 md에서는 25%만 차지하겠다는 의미*/}
        {/*만약 Col 2개를 같이 가로로 두고 싶으면 두 Col의 xs 합을 24 이하로 둬야한다.*/}
        <Col xs={24} md={6}>
          {isLoggedIn ? <userProfile /> : <LoginForm />}
        </Col>
        <Col xs={24} md={12}>
          {children}
        </Col>
        <Col xs={24} md={6}>
          <a
            href="https://shortcoding.tistory.com/"
            target={"_blank"}
            // 밑에 걸 해줘야 새창에서 띄울 때 보안상 안전하다
            rel={"_noreferrer noopener"}
          >
            made by 5hyun
          </a>
        </Col>
      </Row>
    </div>
  );
};
AppLayout.propTypes = {
  children: PropTypes.node.isRequired,
};

export default AppLayout;
